using System;
using GBX.NET;
using GBX.NET.Engines.Game;
using GBX.NET.Engines.Scene;
using GBX.NET.Inputs;
using TmEssentials;
using System.Linq;
using GbxExplorerOld.Client.Sections.SectionEditor;
using KellermanSoftware.CompareNetObjects;

public static class Program
{
    //entrypoint
    public static void Run(Gbx gbx, EditorServices services)
    {
        if (services.GbxService.List.Count != 2) {
            Console.WriteLine("Expected exactly 2 GBX files");
            return;
        }
        var gbx1 = services.GbxService.List[0].Gbx;
        var gbx2 = services.GbxService.List[1].Gbx;

        if (gbx1.GetType() != gbx2.GetType()) {
            Console.WriteLine("Expected GBX files of the same type");
            return;
        }


        var compareLogic = new CompareLogic(new ComparisonConfig() { MaxDifferences = 100 });

        var result = compareLogic.Compare(gbx1, gbx2);

        if (!result.AreEqual)
        {
            Console.WriteLine("Files are not equal:");
            Console.WriteLine();

            foreach (var difference in result.Differences)
            {
                Console.WriteLine(difference.ToString());
            }
        }
        else
        {
            Console.WriteLine("Files are equal.");
        }
    }
}
